import React from "react";
import HotItemCard from "./HotItemCard.js";
import "../styles/HotAccessories.css"

const HotAccessories = ({ music, musicCover, smartDevice, smartDeviceCover, home, homeCover, lifestyle, lifestyleCover, mobileAccessories, mobileAccessoriesCover }) => {
  return (
    <div className="HotAccessories">
      <div>
        <img src={musicCover || smartDeviceCover || homeCover || lifestyleCover ||  mobileAccessoriesCover} alt="Cover" />
      </div>

      <div>
        { music && music.map((item, index) => (
          <HotItemCard 
            key={item.image}
            name={item.name}
            price={item.price}
            image={item.image}
            index={index}
          />
        ))}
        {smartDevice && smartDevice.map((item, index) => (
          <HotItemCard 
            key={item.image}
            name={item.name}
            price={item.price}
            image={item.image}
            index={index}
          />
        ))}
        {home && home.map((item, index) => (
          <HotItemCard 
            key={item.image}
            name={item.name}
            price={item.price}
            image={item.image}
            index={index}
          />
        ))}
         {lifestyle && home.map((item, index) => (
          <HotItemCard 
            key={item.image}
            name={item.name}
            price={item.price}
            image={item.image}
            index={index}
          />
        ))}
      </div>
    </div>
  );
};

export default HotAccessories;
